CommonData.BannerCustomCSS:
#onetrust-banner-sdk p,
#onetrust-banner-sdk a,
#onetrust-banner-sdk span {
    font-family: 'Interstate Light', 'Interstate', Arial, Helvetica, Sans-Serif !important;
    font-size: 14px !important;
    line-height: 20px !important;
}

#onetrust-banner-sdk button {
    font-family: 'Interstate Light', 'Interstate', Arial, Helvetica, Sans-Serif !important;
    font-size: 16px !important;
    line-height: 20px !important;
}

#onetrust-banner-sdk #onetrust-policy-text a, #onetrust-banner-sdk .ot-b-addl-desc a {
    margin-left: 0px;
    color: #ff0000;
}

#onetrust-banner-sdk.otFloatingRounded {
    width: 90%;
    max-width: 800px;
}

#onetrust-banner-sdk #onetrust-button-group {
    float: left;
}

#onetrust-banner-sdk #onetrust-accept-btn-handler, #onetrust-banner-sdk #onetrust-reject-all-handler, #onetrust-banner-sdk #onetrust-pc-btn-handler {
    border-radius: 8px;
    border: none;
    white-space: nowrap;
    max-width: 100%;
}

#onetrust-banner-sdk.otFloatingRounded[dir=rtl] {
    width: 95% !important;
    bottom: 0 !important;
}

#onetrust-banner-sdk b {
    font-family: 'Interstate', 'Interstate', Arial, Helvetica, Sans-Serif !important;
    font-weight: bold;
}

#onetrust-banner-sdk a {
    text-decoration: none;
}
